import{_ as a,c as t,b as p,d as n,e as o,a as e,r as c,o as u}from"./app.144f2fe4.js";const v='{"title":"\u5F02\u6B65 setup","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E00\u4E2A\u4F8B\u5B50","slug":"\u4E00\u4E2A\u4F8B\u5B50"},{"level":2,"title":"\u80CC\u540E\u673A\u5236","slug":"\u80CC\u540E\u673A\u5236"},{"level":2,"title":"\u5F02\u6B65 setup \u7684\u5C40\u9650","slug":"\u5F02\u6B65-setup-\u7684\u5C40\u9650"},{"level":2,"title":"\u89E3\u51B3\u65B9\u6848","slug":"\u89E3\u51B3\u65B9\u6848"},{"level":3,"title":"\u8BB0\u4F4F\u5E76\u907F\u514D\u5B83","slug":"\u8BB0\u4F4F\u5E76\u907F\u514D\u5B83"},{"level":3,"title":"\u663E\u5F0F\u7ED1\u5B9A\u7EC4\u4EF6\u5B9E\u4F8B","slug":"\u663E\u5F0F\u7ED1\u5B9A\u7EC4\u4EF6\u5B9E\u4F8B"},{"level":3,"title":"<script setup> \u7F16\u8BD1\u65F6\u5904\u7406","slug":"script-setup-\u7F16\u8BD1\u65F6\u5904\u7406"}],"relativePath":"articles/async-setup.md"}',l={},i=n("h1",{id:"\u5F02\u6B65-setup",tabindex:"-1"},[o("\u5F02\u6B65 setup "),n("a",{class:"header-anchor",href:"#\u5F02\u6B65-setup","aria-hidden":"true"},"#")],-1),k=e(`<p><code>setup</code> \u662F Vue 3 \u65B0\u589E\u7684\u4E00\u4E2A\u7EC4\u4EF6\u9009\u9879\uFF0C\u5B83\u4F5C\u4E3A Composition API \u7684\u5165\u53E3\u51FD\u6570\uFF0C\u6781\u5927\u5730\u4F18\u5316\u4E86 Vue \u4EE3\u7801\u7684\u7EC4\u7EC7\u65B9\u5F0F\uFF0C\u8BA9\u4EE3\u7801\u903B\u8F91\u590D\u7528\u53D8\u5F97\u975E\u5E38\u7B80\u5355\u548C\u76F4\u89C2\u3002\u800C\u5F02\u6B65\u7684 <code>setup</code> (async setup) \u53EF\u4EE5\u8BA9\u4F60\u5728\u7EC4\u4EF6\u521B\u5EFA\u4E4B\u524D\u8FDB\u884C\u5F02\u6B65\u64CD\u4F5C\uFF0C\u6BD4\u5982\u4ECE\u670D\u52A1\u5668\u83B7\u53D6\u8D44\u6E90\u548C\u6570\u636E\uFF0C\u4F46\u662F\u6709\u4E00\u4E9B\u6CE8\u610F\u4E8B\u9879\uFF0C\u8BE6\u60C5\u8BF7\u770B\u8FD9\u91CC\u7684<a href="https://github.com/vuejs/rfcs/discussions/234" target="_blank" rel="noopener noreferrer">\u8BA8\u8BBA</a>\u3002</p><h2 id="\u4E00\u4E2A\u4F8B\u5B50" tabindex="-1">\u4E00\u4E2A\u4F8B\u5B50 <a class="header-anchor" href="#\u4E00\u4E2A\u4F8B\u5B50" aria-hidden="true">#</a></h2><p>\u5148\u770B\u4E00\u4E2A\u4F8B\u5B50\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">import</span> <span class="token punctuation">{</span> ref<span class="token punctuation">,</span> watch<span class="token punctuation">,</span> onMounted<span class="token punctuation">,</span> onUnmounted <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;vue&#39;</span>

<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token punctuation">{</span>
  <span class="token keyword">async</span> <span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token keyword">const</span> counter <span class="token operator">=</span> <span class="token function">ref</span><span class="token punctuation">(</span><span class="token number">0</span><span class="token punctuation">)</span>

    <span class="token function">watch</span><span class="token punctuation">(</span>counter<span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>counter<span class="token punctuation">.</span>value<span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment">// OK!</span>
    <span class="token function">onMounted</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">&#39;Mounted&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment">// \u7B49\u5F85\u5F02\u6B65\u4EFB\u52A1</span>
    <span class="token keyword">await</span> <span class="token function">someAsyncFunction</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

    <span class="token comment">// \u4E0B\u9762\u4E0D\u4F1A\u6B63\u5E38\u8C03\u7528!</span>
    <span class="token function">onUnmounted</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">&#39;Unmounted&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment">// \u53EF\u4EE5\u6B63\u5E38\u5DE5\u4F5C\uFF0C\u4F46\u662F\u53EF\u80FD\u9020\u6210\u5185\u5B58\u6CC4\u6F0F</span>
    <span class="token comment">// \u56E0\u4E3A\u5728\u7EC4\u4EF6\u88AB\u9500\u6BC1\u540E\u4E0D\u4F1A\u81EA\u52A8 dispose</span>
    <span class="token function">watch</span><span class="token punctuation">(</span>counter<span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>counter<span class="token punctuation">.</span>value <span class="token operator">*</span> <span class="token number">2</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\u5728 <strong>await</strong> statement \u4E4B\u540E\u6267\u884C\u4EE5\u4E0B composition API\uFF0C\u5F88\u6709\u53EF\u80FD\u4F1A\u51FA\u73B0\u95EE\u9898\uFF0C \u56E0\u4E3A\u5B83\u4EEC\u4E0D\u4F1A\u81EA\u52A8 dispose:</p><ul><li><code>watch</code> / <code>watchEffect</code></li><li><code>computed</code></li><li><code>effect</code></li></ul><p>\u800C\u4E0B\u9762\u7684\u8FD9\u4E9B\u65B9\u6CD5\u5C06\u4E0D\u4F1A\u6B63\u5E38\u5DE5\u4F5C\uFF1A</p><ul><li><code>onMounted</code> / <code>onUnmounted</code> / <code>onXXX</code></li><li><code>provide</code> / <code>inject</code></li><li><code>getCurrentInstance</code></li><li>...</li></ul><h2 id="\u80CC\u540E\u673A\u5236" tabindex="-1">\u80CC\u540E\u673A\u5236 <a class="header-anchor" href="#\u80CC\u540E\u673A\u5236" aria-hidden="true">#</a></h2><p>\u4EE5 <code>onMounted</code> \u4E3A\u4F8B\u3002\u5982\u6211\u4EEC\u6240\u77E5\uFF0C<code>onMounted</code> \u662F\u4E00\u4E2A\u94A9\u5B50\u51FD\u6570\uFF08hook\uFF09\uFF0C\u5728\u5F53\u524D\u7EC4\u4EF6\u88AB\u6302\u8F7D\uFF08mounted\uFF09\u65F6\uFF0C\u6267\u884C\u6CE8\u518C\u7684 listener \u51FD\u6570\u3002\u8BF7\u6CE8\u610F\uFF0C<code>onMounted</code>\uFF08\u4EE5\u53CA\u5176\u5B83\u7684\u7EC4\u5408 API\uFF09\u662F\u5168\u5C40\u7684\uFF0C\u8FD9\u91CC\u6240\u8BF4\u7684&quot;\u5168\u5C40&quot;\u662F\u6307\u5B83\u53EF\u4EE5\u88AB\u5BFC\u5165\u5230\u4EFB\u4F55\u6A21\u5757\u4E2D\u88AB\u8C03\u7528\u2014\u2014\u5373\u6CA1\u6709\u672C\u5730\u7684\u4E0A\u4E0B\u6587\u4E0E\u5B83\u7ED1\u5B9A\u3002</p><div class="language-js"><pre><code><span class="token comment">// local: \`onMounted\` \u662F\u7EC4\u4EF6\u5B9E\u4F8B\u7684\u65B9\u6CD5\uFF0C\u7ED1\u5B9A\u5230\u7EC4\u4EF6\u4E0A\u4E0B\u6587</span>
component<span class="token punctuation">.</span><span class="token function">onMounted</span><span class="token punctuation">(</span><span class="token comment">/* ... */</span><span class="token punctuation">)</span>

<span class="token comment">// global: \`onMounted\` \u6CA1\u6709\u7ED1\u5B9A\u4E0A\u4E0B\u6587</span>
<span class="token function">onMounted</span><span class="token punctuation">(</span><span class="token comment">/* ... */</span><span class="token punctuation">)</span>
</code></pre></div><p>\u90A3\u4E48\uFF0C<code>onMounted</code> \u662F\u5982\u4F55\u77E5\u9053\u4EC0\u4E48\u7EC4\u4EF6\u88AB\u6302\u8F7D\u7684\u5462\uFF1F Vue \u4F7F\u7528\u4E00\u4E2A\u5185\u90E8\u53D8\u91CF\u6765\u8BB0\u5F55\u5F53\u524D\u7EC4\u4EF6\u7684\u5B9E\u4F8B\u3002\u5F53 Vue \u6302\u8F7D\u4E00\u4E2A\u7EC4\u4EF6\u65F6\uFF0C\u5B83\u5C06\u8BE5\u7EC4\u4EF6\u5B9E\u4F8B\u5B58\u50A8\u5728\u4E00\u4E2A\u5168\u5C40\u53D8\u91CF\u4E2D\u3002\u5F53\u94A9\u5B50\u5728 setup \u51FD\u6570\u4E2D\u88AB\u8C03\u7528\u65F6\uFF0C\u5B83\u5C06\u4F7F\u7528\u5168\u5C40\u53D8\u91CF\u6765\u83B7\u53D6\u5F53\u524D\u7EC4\u4EF6\u7684\u5B9E\u4F8B\u3002\u4E0B\u9762\u662F\u7B80\u5316\u7684\u4EE3\u7801\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">let</span> currentInstance <span class="token operator">=</span> <span class="token keyword">null</span>

<span class="token comment">// (pseudo code)</span>
<span class="token keyword">export</span> <span class="token keyword">function</span> <span class="token function">mountComponent</span><span class="token punctuation">(</span><span class="token parameter">component</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token keyword">const</span> instance <span class="token operator">=</span> <span class="token function">createComponent</span><span class="token punctuation">(</span>component<span class="token punctuation">)</span>

  <span class="token comment">// \u5148\u4FDD\u5B58\u5148\u524D\u7684\u5B9E\u4F8B</span>
  <span class="token keyword">const</span> prev <span class="token operator">=</span> currentInstance

  <span class="token comment">// \u5C06\u5F53\u524D\u7EC4\u4EF6\u5B9E\u4F8B\u5B58\u5165\u5168\u5C40\u53D8\u91CF</span>
  currentInstance <span class="token operator">=</span> instance

  <span class="token comment">// setup \u5185\u90E8\u7684 hooks \u51FD\u6570\u88AB\u8C03\u7528\u65F6\u5C06\u4EE5 \`currentInstance\` \u4F5C\u4E3A\u4E0A\u4E0B\u6587</span>
  component<span class="token punctuation">.</span><span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

  <span class="token comment">// \u6062\u590D\u5148\u524D\u7684\u5B9E\u4F8B</span>
  currentInstance <span class="token operator">=</span> prev
<span class="token punctuation">}</span>
</code></pre></div><p>\u4E00\u4E2A\u7B80\u5316\u7684 <code>onMounted</code> \u5B9E\u73B0\u53EF\u4EE5\u662F\u8FD9\u6837\uFF1A</p><div class="language-js"><pre><code><span class="token comment">// (pseudo code)</span>
<span class="token keyword">export</span> <span class="token keyword">function</span> <span class="token function">onMounted</span><span class="token punctuation">(</span><span class="token parameter">fn</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token keyword">if</span> <span class="token punctuation">(</span><span class="token operator">!</span>currentInstance<span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token function">warn</span><span class="token punctuation">(</span><span class="token template-string"><span class="token template-punctuation string">\`</span><span class="token string">&quot;onMounted&quot; can&#39;t be called outside of component setup()</span><span class="token template-punctuation string">\`</span></span><span class="token punctuation">)</span>
    <span class="token keyword">return</span>
  <span class="token punctuation">}</span>

  <span class="token comment">// \u5C06 listener \u7ED1\u5B9A\u5230\u5F53\u524D\u7EC4\u4EF6\u5B9E\u4F8B</span>
  currentInstance<span class="token punctuation">.</span><span class="token function">onMounted</span><span class="token punctuation">(</span>fn<span class="token punctuation">)</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\u8FD9\u6837\uFF0C\u53EA\u8981\u5728\u7EC4\u4EF6\u7684 <code>setup</code> \u91CC\u9762\u8C03\u7528 <code>onMounted</code>\uFF0C\u5C31\u80FD\u62FF\u5230\u5F53\u524D\u7EC4\u4EF6\u7684\u5B9E\u4F8B\u3002</p><h2 id="\u5F02\u6B65-setup-\u7684\u5C40\u9650" tabindex="-1">\u5F02\u6B65 setup \u7684\u5C40\u9650 <a class="header-anchor" href="#\u5F02\u6B65-setup-\u7684\u5C40\u9650" aria-hidden="true">#</a></h2><p>\u5982\u679C <code>setup</code> \u662F\u540C\u6B65\u7684\uFF0C\u90A3\u4E00\u5207\u90FD\u4FDD\u6301\u6B63\u5E38\uFF0C\u8FD9\u662F\u57FA\u4E8E JavaScript \u662F\u5355\u7EBF\u7A0B\u7684\u8FD9\u4E00\u4E8B\u5B9E\u3002\u5355\u7EBF\u7A0B\u7684\u539F\u5B50\u6027\u786E\u4FDD\u4EE5\u4E0B\u8BED\u53E5\u4F1A\u7D27\u6328\u7740\u6267\u884C\uFF0C\u6362\u53E5\u8BDD\u8BF4\uFF0C\u4F60\u4E0D\u53EF\u80FD\u5728\u540C\u4E00\u65F6\u95F4\u610F\u5916\u5730\u4FEE\u6539 currentInstance\uFF1A</p><div class="language-js"><pre><code>currentInstance <span class="token operator">=</span> instance
component<span class="token punctuation">.</span><span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
currentInstance <span class="token operator">=</span> prev
</code></pre></div><p>\u4F46\u5F53 <code>setup</code> \u51FD\u6570\u662F\u5F02\u6B65\u7684\u65F6\u5019\uFF0C\u60C5\u51B5\u5C31\u53D8\u4E86\u3002\u6BCF\u5F53 await \u4E00\u4E2A promise \u65F6\uFF0C\u4F60\u53EF\u4EE5\u8BA4\u4E3A JS \u5F15\u64CE\u6682\u505C\u4E86\u8FD9\u91CC\u7684\u5DE5\u4F5C\uFF0C\u53BB\u505A\u53E6\u4E00\u4E2A\u4EFB\u52A1\u3002\u800C\u5728\u8FD9\u4E2A\u7B49\u5F85\u7684\u65F6\u95F4\u6BB5\u5185\uFF0C\u539F\u5B50\u6027\u4F1A\u4E22\u5931\uFF0C\u5176\u5B83\u7EC4\u4EF6\u7684\u521B\u5EFA\u5C06\u4E0D\u53EF\u9884\u6D4B\u5730\u4F1A\u6539\u53D8\u5168\u5C40\u53D8\u91CF\uFF0C\u6700\u7EC8\u5BFC\u81F4\u6DF7\u4E71:</p><div class="language-js"><pre><code><span class="token keyword">async</span> <span class="token keyword">function</span> <span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">)</span>
  <span class="token keyword">await</span> <span class="token function">someAsyncFunction</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
  console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span>

console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token number">3</span><span class="token punctuation">)</span>
<span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token number">4</span><span class="token punctuation">)</span>

<span class="token comment">// \u8F93\u51FA</span>
<span class="token comment">// 3</span>
<span class="token comment">// 1</span>
<span class="token comment">// 4</span>
<span class="token comment">// (awaiting)</span>
<span class="token comment">// 2</span>
</code></pre></div><p>\u5F02\u6B65\u7684 <code>setup</code> \u51FD\u6570\u4E0D\u4F1A\u963B\u585E\u540E\u9762\u7684\u4EFB\u52A1, \u4F46 <code>setup</code> \u5185\u7684\u7B2C\u4E00\u4E2A await statement \u4E4B\u540E\u7684\u4EE3\u7801\uFF0C\u5C06\u5728\u5F02\u6B65\u4EFB\u52A1\u5B8C\u6210\u4E4B\u540E\u624D\u4F1A\u88AB\u6267\u884C\uFF0C \u8FD9\u65F6 <code>setup</code> \u51FD\u6570\u5DF2\u7ECF return\uFF0C\u8FD9\u610F\u5473\u7740\u7B2C\u4E00\u4E2A await statement \u4E4B\u540E\u7684\u4EE3\u7801\u5C06\u62FF\u4E0D\u5230\u5F53\u524D\u7EC4\u4EF6\u5B9E\u4F8B\u3002</p><h2 id="\u89E3\u51B3\u65B9\u6848" tabindex="-1">\u89E3\u51B3\u65B9\u6848 <a class="header-anchor" href="#\u89E3\u51B3\u65B9\u6848" aria-hidden="true">#</a></h2><h3 id="\u8BB0\u4F4F\u5E76\u907F\u514D\u5B83" tabindex="-1">\u8BB0\u4F4F\u5E76\u907F\u514D\u5B83 <a class="header-anchor" href="#\u8BB0\u4F4F\u5E76\u907F\u514D\u5B83" aria-hidden="true">#</a></h3><p>\u5F53\u7136\uFF0C\u8FD9\u662F\u4E00\u4E2A\u663E\u800C\u6613\u89C1\u7684\u89E3\u51B3\u65B9\u6848\u3002\u5C06\u6240\u6709\u7684 effect \u548C hooks \u79FB\u5230\u7B2C\u4E00\u4E2A await statement \u4E4B\u524D\uFF0C\u5E76\u4E14\u8BB0\u4F4F\u5728\u90A3\u4E4B\u540E\u4E0D\u8981\u518D\u4F7F\u7528\u5B83\u4EEC\u3002</p><p>\u5E78\u8FD0\u7684\u662F\uFF0C\u5982\u679C\u4F60\u4F7F\u7528 ESLint\uFF0C\u53EF\u4EE5\u542F\u7528 eslint-plugin-vue \u4E2D\u7684 <code>vue/no-watch-after-await</code> \u548C <code>vue/no-lifecycle-after-await</code> \u89C4\u5219\uFF0C\u4EE5\u4FBF\u5728\u51FA\u73B0\u9519\u8BEF\u65F6\u53D1\u51FA\u8B66\u544A\uFF08\u9ED8\u8BA4\u60C5\u51B5\u4E0B\uFF0C\u63D2\u4EF6\u9884\u8BBE\u4E2D\u4F1A\u542F\u7528\u8FD9\u4E9B\u89C4\u5219\uFF09\u3002</p><h3 id="\u663E\u5F0F\u7ED1\u5B9A\u7EC4\u4EF6\u5B9E\u4F8B" tabindex="-1">\u663E\u5F0F\u7ED1\u5B9A\u7EC4\u4EF6\u5B9E\u4F8B <a class="header-anchor" href="#\u663E\u5F0F\u7ED1\u5B9A\u7EC4\u4EF6\u5B9E\u4F8B" aria-hidden="true">#</a></h3><p>\u751F\u547D\u5468\u671F\u94A9\u5B50\uFF08lifecycle hooks\uFF09\u5B9E\u9645\u4E0A\u63A5\u53D7\u7B2C\u4E8C\u4E2A\u53C2\u6570\u6765\u663E\u5F0F\u7684\u7ED1\u5B9A\u5B9E\u4F8B\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token keyword">async</span> <span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token comment">// \u5728 await \u4E4B\u524D\u5148\u83B7\u53D6\u7EC4\u4EF6\u5B9E\u4F8B</span>
    <span class="token keyword">const</span> instance <span class="token operator">=</span> <span class="token function">getCurrentInstance</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

    <span class="token keyword">await</span> <span class="token function">someAsyncFunction</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

    <span class="token function">onUnmounted</span><span class="token punctuation">(</span>
      <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">&#39;Unmounted&#39;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
      instance <span class="token comment">// &lt;--- \u624B\u52A8\u7ED9\u94A9\u5B50\u51FD\u6570\u7ED1\u5B9A\u5B9E\u4F8B</span>
    <span class="token punctuation">)</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p>\u4F46\u662F\uFF0C\u7F3A\u70B9\u662F\u6B64\u89E3\u51B3\u65B9\u6848\u4E0D\u9002\u7528\u4E8E <code>watch/watchEffect/computed/provide/inject</code>\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u4E0D\u63A5\u53D7\u5B9E\u4F8B\u53C2\u6570\u3002 \u8981\u4F7F\u8FD9\u4E9B\u6B63\u5E38\u5DE5\u4F5C\uFF0C\u53EF\u4EE5\u4F7F\u7528 Vue 3.2 \u4E2D\u65B0\u5F15\u5165\u7684 <a href="https://github.com/vuejs/rfcs/blob/master/active-rfcs/0041-reactivity-effect-scope.md" target="_blank" rel="noopener noreferrer">effectScope API</a>\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">import</span> <span class="token punctuation">{</span> effectScope <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;vue&#39;</span>

<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token keyword">async</span> <span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token comment">// \u5728 await\u4E4B\u524D\u521B\u5EFA\u4E00\u4E2A scope, \u5B83\u4F1A\u5C06\u7EC4\u4EF6\u5B9E\u4F8B\u7ED1\u5B9A\u5230\u8FD9\u4E2A\u4F5C\u7528\u57DF\u5185</span>
    <span class="token keyword">const</span> scope <span class="token operator">=</span> <span class="token function">effectScope</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

    <span class="token keyword">const</span> data <span class="token operator">=</span> <span class="token keyword">await</span> <span class="token function">someAsyncFunction</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token comment">// &lt;-----------</span>

    scope<span class="token punctuation">.</span><span class="token function">run</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
      <span class="token comment">/* Use \`computed\`, \`watch\`, etc. ... */</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><h3 id="script-setup-\u7F16\u8BD1\u65F6\u5904\u7406" tabindex="-1"><code>&lt;script setup&gt;</code> \u7F16\u8BD1\u65F6\u5904\u7406 <a class="header-anchor" href="#script-setup-\u7F16\u8BD1\u65F6\u5904\u7406" aria-hidden="true">#</a></h3><p>\u5728\u6700\u8FD1\u7684 <code>&lt;script setup&gt;</code> \u8BED\u6CD5<a href="https://github.com/vuejs/rfcs/blob/master/active-rfcs/0040-script-setup.md#top-level-await" target="_blank" rel="noopener noreferrer">\u63D0\u6848</a>\u4E2D\uFF08Vue 3.2 \u53EF\u7528\uFF09\uFF0C\u9488\u5BF9\u4E5F\u8FD9\u4E00\u95EE\u9898\u8FDB\u884C\u4E86\u7F16\u8BD1\u65F6\u7684\u5904\u7406\uFF1A</p><div class="language-"><pre><code>&lt;script setup&gt;
  const post = await fetch(\`/api/post/1\`).then((r) =&gt; r.json())
&lt;/script&gt;
</code></pre></div><p>await \u8BED\u53E5\u5C06\u81EA\u52A8\u7F16\u8BD1\u4E3A\u5728 await \u8BED\u53E5\u4E4B\u540E\u4FDD\u7559\u5F53\u524D\u7EC4\u4EF6\u5B9E\u4F8B\u4E0A\u4E0B\u6587\u7684\u683C\u5F0F\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">import</span> <span class="token punctuation">{</span> withAsyncContext <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;vue&#39;</span>

<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token punctuation">{</span>
  <span class="token keyword">async</span> <span class="token function">setup</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    <span class="token keyword">let</span> __temp<span class="token punctuation">,</span> __restore

    <span class="token keyword">const</span> post <span class="token operator">=</span>
      <span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">[</span>__temp<span class="token punctuation">,</span> __restore<span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token function">withAsyncContext</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span>
        <span class="token function">fetch</span><span class="token punctuation">(</span><span class="token template-string"><span class="token template-punctuation string">\`</span><span class="token string">/api/post/1</span><span class="token template-punctuation string">\`</span></span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">then</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token parameter">r</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> r<span class="token punctuation">.</span><span class="token function">json</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
      <span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
      <span class="token punctuation">(</span>__temp <span class="token operator">=</span> <span class="token keyword">await</span> __temp<span class="token punctuation">)</span><span class="token punctuation">,</span>
      <span class="token function">__restore</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
      __temp<span class="token punctuation">)</span>

    <span class="token comment">// current instance context preserved</span>
    <span class="token comment">// e.g. onMounted() will still work.</span>

    <span class="token keyword">return</span> <span class="token punctuation">{</span> post <span class="token punctuation">}</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

</code></pre></div><p>\u6709\u4E86\u5B83\uFF0C\u5F02\u6B65\u51FD\u6570\u5C31\u53EF\u4EE5\u5728\u4E0E<code>&lt;script setup&gt;</code>\u4E00\u8D77\u4F7F\u7528\u65F6\u6B63\u5E38\u5DE5\u4F5C\u3002\u552F\u4E00\u7684\u9057\u61BE\u662F\u5B83\u5728<code>&lt;script setup&gt;</code>\u4E4B\u5916\u65E0\u6CD5\u5DE5\u4F5C\u3002</p>`,37);function r(d,m,f,g,h,w){const s=c("PubDate");return u(),t("div",null,[i,p(s,{date:"2021/09/21"}),k])}var _=a(l,[["render",r]]);export{v as __pageData,_ as default};
