import{_ as a,c as e,b as t,d as s,e as p,a as o,r as c,o as l}from"./app.ace6221f.js";var r="/yangss/assets/ts-subtype.875029f9.svg";const T='{"title":"\u6DF1\u5165\u7406\u89E3 TypeScript \u7684\u7C7B\u578B\u7CFB\u7EDF","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u628A\u7C7B\u578B\u770B\u4F5C\u503C\u7684\u96C6\u5408","slug":"\u628A\u7C7B\u578B\u770B\u4F5C\u503C\u7684\u96C6\u5408"},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3"}],"relativePath":"articles/understand-ts-type-system.md"}',k={},d=s("h1",{id:"\u6DF1\u5165\u7406\u89E3-typescript-\u7684\u7C7B\u578B\u7CFB\u7EDF",tabindex:"-1"},[p("\u6DF1\u5165\u7406\u89E3 TypeScript \u7684\u7C7B\u578B\u7CFB\u7EDF "),s("a",{class:"header-anchor",href:"#\u6DF1\u5165\u7406\u89E3-typescript-\u7684\u7C7B\u578B\u7CFB\u7EDF","aria-hidden":"true"},"#")],-1),i=o(`<h2 id="\u628A\u7C7B\u578B\u770B\u4F5C\u503C\u7684\u96C6\u5408" tabindex="-1">\u628A\u7C7B\u578B\u770B\u4F5C\u503C\u7684\u96C6\u5408 <a class="header-anchor" href="#\u628A\u7C7B\u578B\u770B\u4F5C\u503C\u7684\u96C6\u5408" aria-hidden="true">#</a></h2><p>\u628A TypeScript \u4E2D\u7684\u7C7B\u578B\u770B\u4F5C\u4E00\u7EC4\u53EF\u80FD\u7684\u503C\u7684\u96C6\u5408\uFF0C\u8FD9\u6709\u52A9\u4E8E\u4F60\u66F4\u597D\u7684\u7406\u89E3 TypeScript \u4E2D\u7684\u7C7B\u578B\u548C\u7C7B\u578B\u64CD\u4F5C\u3002</p><p>\u4F8B\u5982\uFF0C\u4F60\u53EF\u4EE5\u628A <code>number</code> \u7C7B\u578B\u770B\u6210\u662F\u6240\u6709\u6570\u503C\u7684\u96C6\u5408\u3002<code>42</code> \u548C <code>-37</code> \u90FD\u5728\u5176\u4E2D\uFF0C\u4F46\u662F <code>&#39;hello&#39;</code> \u4E0D\u5728\u5176\u4E2D\u3002\u6839\u636E\u662F\u5426\u8BBE\u7F6E <code>strictNullChecks</code>\uFF0C<code>null</code> \u548C <code>undefined</code> \u53EF\u80FD\u662F\u4E5F\u53EF\u80FD\u4E0D\u662F\u8FD9\u4E2A\u96C6\u5408\u7684\u4E00\u90E8\u5206\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">let</span> num<span class="token operator">:</span> <span class="token builtin">number</span> <span class="token operator">=</span> <span class="token number">42</span> <span class="token comment">// ok</span>
num <span class="token operator">=</span> <span class="token operator">-</span><span class="token number">37</span> <span class="token comment">// ok</span>
num <span class="token operator">=</span> <span class="token string">&#39;hello&#39;</span> <span class="token comment">// ~ Type string is not assignable to type number</span>
</code></pre></div><p>\u6700\u5C0F\u7684\u96C6\u5408\u662F\u7A7A\u96C6\uFF0C\u5B83\u5BF9\u5E94\u4E8E TypeScript \u4E2D\u7684 <code>never</code> \u7C7B\u578B\u3002\u56E0\u4E3A\u7A7A\u96C6\u4E0D\u5305\u542B\u4EFB\u4F55\u503C\uFF0C\u6240\u4EE5\u4EFB\u4F55\u503C\u90FD\u4E0D\u80FD\u8D4B\u503C\u7ED9\u4E00\u4E2A\u5177\u6709 <code>never</code> \u7C7B\u578B\u7684\u53D8\u91CF\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">const</span> x<span class="token operator">:</span> <span class="token builtin">never</span> <span class="token operator">=</span> <span class="token number">12</span> <span class="token comment">// ~ Type &#39;12&#39; is not assignable to type &#39;never&#39;</span>
</code></pre></div><p>\u5176\u6B21\u6700\u5C0F\u7684\u96C6\u5408\u5C31\u662F\u53EA\u5305\u542B\u4E00\u4E2A\u503C\u7684\u96C6\u5408\uFF0C\u8FD9\u5BF9\u5E94\u4E8E TypeScript \u4E2D\u7684\u5B57\u9762\u7C7B\u578B(literal type\uFF09\uFF0C\u4F8B\u5982\uFF1A</p><div class="language-js"><pre><code>type <span class="token constant">A</span> <span class="token operator">=</span> <span class="token string">&#39;A&#39;</span>
type <span class="token constant">B</span> <span class="token operator">=</span> <span class="token string">&#39;B&#39;</span>
type Three <span class="token operator">=</span> <span class="token number">3</span>
</code></pre></div><p>\u53EF\u4EE5\u5C06\u591A\u4E2A\u96C6\u5408\u6C42\u5E76\u96C6\u751F\u6210\u4E00\u4E2A\u65B0\u7684\u96C6\u5408\uFF0C\u8FD9\u5BF9\u5E94\u4E8E TypeScript \u4E2D\u7684\u8054\u5408\u7C7B\u578B\uFF0C\u7C7B\u578B\u64CD\u4F5C <code>|</code> \u53EF\u4EE5\u7406\u89E3\u4E3A\u96C6\u5408\u6C42\u5E76\u96C6\u64CD\u4F5C\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">type</span> <span class="token class-name"><span class="token constant">AB</span></span> <span class="token operator">=</span> <span class="token string">&#39;A&#39;</span> <span class="token operator">|</span> <span class="token string">&#39;B&#39;</span>
<span class="token keyword">type</span> <span class="token class-name"><span class="token constant">AB3</span></span> <span class="token operator">=</span> <span class="token string">&#39;A&#39;</span> <span class="token operator">|</span> <span class="token string">&#39;B&#39;</span> <span class="token operator">|</span> <span class="token number">3</span>
</code></pre></div><p>\u7C7B\u578B B \u80FD\u8D4B\u7ED9\uFF08assignable to\uFF09\u7C7B\u578B A\uFF0C\u5F53\u4E14\u4EC5\u5F53\u7C7B\u578B B \u4EE3\u8868\u7684\u96C6\u5408\u662F\u7C7B\u578B A \u4EE3\u8868\u7684\u96C6\u5408\u7684\u5B50\u96C6\uFF08subset\uFF09\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u8FD9\u6B63\u662F TypeScript \u7684\u7C7B\u578B\u68C0\u67E5\u7684\u672C\u8D28\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">const</span> ab<span class="token operator">:</span> <span class="token constant">AB</span> <span class="token operator">=</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">&gt;</span> <span class="token number">0.5</span> <span class="token operator">?</span> <span class="token string">&#39;A&#39;</span> <span class="token operator">:</span> <span class="token string">&#39;B&#39;</span> <span class="token comment">// OK, {&quot;A&quot;, &quot;B&quot;} \u662F {&quot;A&quot;, &quot;B&quot;} \u7684\u5B50\u96C6</span>
<span class="token keyword">const</span> ab3<span class="token operator">:</span> <span class="token constant">AB3</span> <span class="token operator">=</span> ab <span class="token comment">// OK, {&quot;A&quot;, &quot;B&quot;} \u662F {&quot;A&quot;, &quot;B&quot;, 3} \u7684\u5B50\u96C6</span>

<span class="token keyword">declare</span> <span class="token keyword">let</span> three<span class="token operator">:</span> <span class="token constant">AB3</span>
<span class="token keyword">const</span> back<span class="token operator">:</span> <span class="token constant">AB</span> <span class="token operator">=</span> three <span class="token comment">// Error, {&quot;A&quot;, &quot;B&quot;, 3} \u4E0D\u662F {&quot;A&quot;, &quot;B&quot;} \u7684\u5B50\u96C6</span>
</code></pre></div><p>\u4E0A\u9762\u8FD9\u4E9B\u4F8B\u5B50\u90FD\u5F88\u5BB9\u6613\u7406\u89E3\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u7C7B\u578B\u4EE3\u8868\u7684\u96C6\u5408\u90FD\u662F\u6709\u9650\u96C6\uFF0C\u4F46\u662F\u5728\u5B9E\u9645\u573A\u666F\u4E2D\u6211\u4EEC\u7528\u5230\u7684\u7C7B\u578B\u5F88\u591A\u662F\u65E0\u9650\u96C6\uFF0C\u8981\u7406\u89E3\u8FD9\u4E9B\u7C7B\u578B\u548C\u7C7B\u578B\u64CD\u4F5C\uFF0C\u5C31\u4E0D\u90A3\u4E48\u5BB9\u6613\u3002\u4F8B\u5982\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">interface</span> <span class="token class-name">Identified</span> <span class="token punctuation">{</span>
  id<span class="token operator">:</span> <span class="token builtin">string</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\u8FD9\u4E2A interface \u5B9A\u4E49\u7684\u96C6\u5408\u5C31\u662F\u4E00\u4E2A\u65E0\u9650\u96C6\uFF0C\u5B83\u63CF\u8FF0\u7684\u662F\u96C6\u5408\u91CC\u7684\u6BCF\u4E2A\u5143\u7D20\u7684\u5F62\u72B6\uFF0C\u5373\u5FC5\u987B\u5305\u542B\u4E00\u4E2A <code>id</code> \u5C5E\u6027\uFF0C\u4E14\u8FD9\u4E2A\u5C5E\u6027\u7684\u503C\u7684\u7C7B\u578B\u5FC5\u987B\u662F <code>string</code> \u7C7B\u578B\u7684\u5B50\u96C6\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u53EA\u8981\u4E00\u4E2A\u5BF9\u8C61\u6EE1\u8DB3\u8FD9\u4E00\u70B9\uFF0C\u5B83\u5C31\u5C5E\u4E8E\u8FD9\u4E2A\u96C6\u5408\uFF0C\u5373\u4F7F\u5B83\u8FD8\u5305\u542B\u989D\u5916\u7684\u5C5E\u6027\u3002</p><p>\u7406\u89E3\u4E86\u8FD9\u4E00\u70B9\u53EF\u4EE5\u5E2E\u52A9\u4F60\u66F4\u597D\u7684\u7406\u89E3\u5728\u8FD9\u4E9B\u7C7B\u578B\u4E0A\u7684\u7C7B\u578B\u64CD\u4F5C\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">interface</span> <span class="token class-name">Person</span> <span class="token punctuation">{</span>
  name<span class="token operator">:</span> <span class="token builtin">string</span>
<span class="token punctuation">}</span>
<span class="token keyword">interface</span> <span class="token class-name">Lifespan</span> <span class="token punctuation">{</span>
  birth<span class="token operator">:</span> Date
  death<span class="token operator">?</span><span class="token operator">:</span> Date
<span class="token punctuation">}</span>
<span class="token keyword">type</span> <span class="token class-name">PersonSpan</span> <span class="token operator">=</span> Person <span class="token operator">&amp;</span> Lifespan
</code></pre></div><p><code>&amp;</code> \u64CD\u4F5C\u7B26\u7528\u6765\u8BA1\u7B97\u4E24\u4E2A\u7C7B\u578B\u7684\u4EA4\u96C6\uFF0C\u90A3\u4E48\u54EA\u4E9B\u503C\u662F\u5C5E\u4E8E <code>PersonSpan</code> \u7C7B\u578B\u5462\uFF1F\u548B\u4E00\u770B\uFF0C<code>Person</code> \u548C <code>Lifespan</code> \u6CA1\u6709\u76F8\u540C\u7684\u5C5E\u6027\uFF0C\u6240\u4EE5\u5B83\u4EEC\u7684\u4EA4\u96C6\u5E94\u8BE5\u662F\u7A7A\u96C6\u3002\u662F\u8FD9\u6837\u5417\uFF1F</p><p>\u7B54\u6848\u662F\u5426\u5B9A\u7684\uFF0C\u9996\u5148\uFF0C\u6211\u4EEC\u8981\u5F04\u6E05\u695A\u7C7B\u578B\u64CD\u4F5C\u662F\u4F5C\u7528\u5728<strong>\u503C\u7684\u96C6\u5408</strong>\u4E0A\uFF0C\u800C\u4E0D\u662F\u7C7B\u578B\u5B9A\u4E49\u7684\u5C5E\u6027\u4E0A\uFF0C\u5176\u6B21\uFF0C\u4E00\u4E2A\u5305\u542B\u989D\u5916\u5C5E\u6027\uFF08\u7C7B\u578B\u5B9A\u4E49\u91CC\u58F0\u660E\u7684\u5C5E\u6027\u4E4B\u5916\u7684\u5C5E\u6027\uFF09\u7684\u503C\u540C\u6837\u5C5E\u4E8E\u8FD9\u4E2A\u7C7B\u578B\u7684\u96C6\u5408\u3002\u6240\u4EE5\u5728\u4E0A\u9762\u7684\u4F8B\u5B50\u4E2D\uFF0C\u5982\u679C\u4E00\u4E2A\u503C\u540C\u65F6\u5305\u542B <code>Person</code> \u548C <code>Lifespan</code> \u7684\u5C5E\u6027\uFF0C\u90A3\u4E48\u5B83\u5C31\u5C5E\u4E8E <code>PersonSpan</code> \u7C7B\u578B\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">const</span> ps<span class="token operator">:</span> PersonSpan <span class="token operator">=</span> <span class="token punctuation">{</span>
  name<span class="token operator">:</span> <span class="token string">&#39;Alan Turing&#39;</span><span class="token punctuation">,</span>
  birth<span class="token operator">:</span> <span class="token keyword">new</span> <span class="token class-name">Date</span><span class="token punctuation">(</span><span class="token string">&#39;1912/06/23&#39;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
  death<span class="token operator">:</span> <span class="token keyword">new</span> <span class="token class-name">Date</span><span class="token punctuation">(</span><span class="token string">&#39;1954/06/07&#39;</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span> <span class="token comment">// Ok</span>
</code></pre></div><p>\u4E00\u822C\u7684\u89C4\u5219\u662F\uFF0C\u591A\u4E2A interface \u7684\u4EA4\u53C9\u7C7B\u578B\uFF08intersection type\uFF09\u4E2D\u7684\u503C\u6240\u5305\u542B\u7684\u5C5E\u6027\uFF0C\u662F\u8FD9\u6BCF\u4E2A\u7EC4\u6210\u7684 interface \u4E2D\u7684\u5C5E\u6027\u7684\u8054\u5408\uFF08union\uFF09\u3002\u53EF\u4EE5\u7528\u4E0B\u9762\u7684\u516C\u5F0F\u6765\u8868\u8FBE\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">keyof</span> <span class="token punctuation">(</span><span class="token constant">A</span> <span class="token operator">&amp;</span> <span class="token constant">B</span><span class="token punctuation">)</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token keyword">keyof</span> <span class="token constant">A</span><span class="token punctuation">)</span> <span class="token operator">|</span> <span class="token punctuation">(</span><span class="token keyword">keyof</span> <span class="token constant">B</span><span class="token punctuation">)</span> <span class="token comment">// (1)</span>
</code></pre></div><p>\u8FD9\u5F88\u5BB9\u6613\u7406\u89E3\uFF0C\u4E5F\u662F\u7B26\u5408\u76F4\u89C9\u7684\u3002\u4F46\u662F\u5BF9\u4E8E\u591A\u4E2A interface \u7684\u8054\u5408\u7C7B\u578B\uFF08union type\uFF09\u6765\u8BF4\uFF0C\u5C31\u6709\u70B9\u7ED5\u4E86\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">type</span> <span class="token class-name"><span class="token constant">K</span></span> <span class="token operator">=</span> <span class="token keyword">keyof</span> <span class="token punctuation">(</span>Person <span class="token operator">|</span> Lifespan<span class="token punctuation">)</span> <span class="token comment">// type K = never</span>
</code></pre></div><p>\u4E3A\u4EC0\u4E48\u662F <code>never</code>\uFF1F\u524D\u9762\u6211\u4EEC\u8BF4\u8FC7\u8054\u5408\u64CD\u4F5C <code>|</code> \u76F8\u5F53\u4E8E\u6C42\u5E76\u96C6\uFF0C<code>Person | Lifespan</code> \u6240\u4EE3\u8868\u7684\u96C6\u5408\u7531\u6240\u6709\u5C5E\u4E8E <code>Person</code> \u7684\u503C\u548C\u6240\u6709\u5C5E\u4E8E <code>Lifespan</code> \u503C\u7EC4\u6210\uFF0C\u4F8B\u5982\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">const</span> p1 <span class="token operator">=</span> <span class="token punctuation">{</span> name<span class="token operator">:</span> <span class="token string">&#39;Rose&#39;</span> <span class="token punctuation">}</span>
<span class="token keyword">const</span> p2 <span class="token operator">=</span> <span class="token punctuation">{</span> name<span class="token operator">:</span> <span class="token string">&#39;Monica&#39;</span><span class="token punctuation">,</span> age<span class="token operator">:</span> <span class="token number">23</span> <span class="token punctuation">}</span>
<span class="token keyword">const</span> l1 <span class="token operator">=</span> <span class="token punctuation">{</span>
 birth<span class="token operator">:</span> <span class="token keyword">new</span> <span class="token class-name">Date</span><span class="token punctuation">(</span><span class="token string">&#39;1932/06/23&#39;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
 death<span class="token operator">:</span> <span class="token keyword">new</span> <span class="token class-name">Date</span><span class="token punctuation">(</span><span class="token string">&#39;1992/03/21&#39;</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span>
<span class="token keyword">const</span> l2 <span class="token operator">=</span> <span class="token punctuation">{</span>
 birth<span class="token operator">:</span> <span class="token keyword">new</span> <span class="token class-name">Date</span><span class="token punctuation">(</span><span class="token string">&#39;1912/06/23&#39;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
 death<span class="token operator">:</span> <span class="token keyword">new</span> <span class="token class-name">Date</span><span class="token punctuation">(</span><span class="token string">&#39;1952/03/21&#39;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
 gender<span class="token operator">:</span> <span class="token string">&#39;male&#39;</span>
<span class="token punctuation">}</span>

<span class="token keyword">let</span> p<span class="token operator">:</span> Person <span class="token operator">|</span> Lifespan <span class="token operator">=</span> p1 <span class="token comment">// Ok</span>
p <span class="token operator">=</span> p2 <span class="token comment">// Ok</span>
p <span class="token operator">=</span> l1 <span class="token comment">// Ok</span>
p <span class="token operator">=</span> l2 <span class="token comment">// Ok</span>
</code></pre></div><p>\u53EF\u4EE5\u770B\u5230 <code>p1</code>\uFF0C<code>p2</code>\uFF0C<code>l1</code>\uFF0C<code>l2</code> \u90FD\u5C5E\u4E8E <code>Person | Lifespan</code> \u7C7B\u578B\uFF0C\u4F46\u662F\u7531\u4E8E <code>Person</code> \u548C <code>Lifespan</code> \u7684\u63A5\u53E3\u5B9A\u4E49\u4E2D\u6240\u63CF\u8FF0\u7684\u5C5E\u6027\u6CA1\u6709\u91CD\u53E0\u7684\u90E8\u5206\uFF0C\u6240\u4EE5 TypeScript \u65E0\u6CD5\u627E\u5230\u67D0\u4E00\u4E2A\u5C5E\u6027\u662F <code>Person | Lifespan</code> \u8FD9\u4E2A\u96C6\u5408\u4E2D\u7684\u6BCF\u4E00\u4E2A\u503C\u90FD\u5305\u542B\u7684\uFF0C\u4F8B\u5982\u4E0A\u9762\u7684 <code>p1</code>\uFF0C<code>p2</code> \u548C <code>l1</code>\uFF0C<code>l2</code> \u4E4B\u95F4\u6CA1\u6709\u5171\u540C\u7684\u5C5E\u6027\uFF0C\u4F46\u662F\u4ED6\u4EEC\u90FD\u5C5E\u4E8E <code>Person | Lifespan</code>\uFF0C\u6240\u4EE5 TypeScript \u63A8\u65AD\u51FA <code>keyof (Person | Lifespan)</code> \u662F\u7A7A\u96C6\uFF0C\u5373 <code>never</code>\u3002</p><p>\u66F4\u901A\u7528\u7684\u89C4\u5219\uFF0C\u4E5F\u53EF\u4EE5\u7528\u4E0B\u9762\u7684\u516C\u5F0F\u6765\u8868\u8FBE\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">keyof</span> <span class="token punctuation">(</span><span class="token constant">A</span> <span class="token operator">|</span> <span class="token constant">B</span><span class="token punctuation">)</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token keyword">keyof</span> <span class="token constant">A</span><span class="token punctuation">)</span> <span class="token operator">&amp;</span> <span class="token punctuation">(</span><span class="token keyword">keyof</span> <span class="token constant">B</span><span class="token punctuation">)</span> <span class="token comment">// (2)</span>
</code></pre></div><p>\u5982\u679C\u4F60\u80FD\u5BF9 (1)\uFF0C(2) \u8FD9\u4E24\u4E2A\u516C\u5F0F\u5EFA\u7ACB\u76F4\u89C9\uFF0C\u90A3\u4E48\u4F60\u5C31\u671D\u7740\u6DF1\u5165\u7406\u89E3 TypeScript \u7684\u7C7B\u578B\u7CFB\u7EDF\u524D\u8FDB\u4E86\u4E00\u5927\u6B65\u3002</p><p>\u53E6\u4E00\u4E2A\u8868\u8FBE <code>PersonSpan</code> \u7C7B\u578B\u7684\u65B9\u6CD5\u662F\u4F7F\u7528 <code>extends</code>\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">interface</span> <span class="token class-name">Person</span> <span class="token punctuation">{</span>
  name<span class="token operator">:</span> <span class="token builtin">string</span>
<span class="token punctuation">}</span>
<span class="token keyword">interface</span> <span class="token class-name">PersonSpan</span> <span class="token keyword">extends</span> <span class="token class-name">Person</span> <span class="token punctuation">{</span>
  birth<span class="token operator">:</span> Date
  death<span class="token operator">?</span><span class="token operator">:</span> Date
<span class="token punctuation">}</span>
</code></pre></div><p>\u5982\u679C\u628A\u7C7B\u578B\u770B\u4F5C\u503C\u7684\u96C6\u5408\uFF0C\u90A3\u8FD9\u91CC\u7684 <code>extends</code> \u8BE5\u5982\u4F55\u7406\u89E3\uFF1F\u5F88\u7B80\u5355\uFF0C<code>A extends B</code>\uFF0C\u53EF\u4EE5\u76F4\u63A5\u7406\u89E3\u4E3A <code>A</code> \u662F <code>B</code> \u7684\u5B50\u96C6\uFF08subset\uFF09\u3002<code>PersonSpan</code> \u4E2D\u7684\u503C\u5FC5\u987B\u5305\u542B <code>name</code> \u5C5E\u6027\u4E14\u5B83\u7684\u503C\u662F\u4E2A <code>string</code>\uFF0C\u540C\u65F6\u4E5F\u5FC5\u987B\u5305\u542B <code>birth</code> \u5C5E\u6027\u3002\u6240\u4EE5 <code>PersonSpan</code> \u662F\u4E00\u4E2A\u7B26\u5408\u8981\u6C42\u7684\u5B50\u96C6\u3002\u4F60\u53EF\u80FD\u8FD8\u542C\u8FC7\u5B50\u7C7B\u578B\uFF08subtype\uFF09\u7684\u8BF4\u6CD5\uFF0C\u5B83\u548C\u8FD9\u91CC\u5B50\u96C6\uFF08subset\uFF09\u8868\u8FBE\u7684\u610F\u601D\u76F8\u540C\u3002\u770B\u4E0B\u9762\u7684\u4F8B\u5B50\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">interface</span> <span class="token class-name">Vector1D</span> <span class="token punctuation">{</span> x<span class="token operator">:</span> <span class="token builtin">number</span> <span class="token punctuation">}</span>
<span class="token keyword">interface</span> <span class="token class-name">Vector2D</span> <span class="token keyword">extends</span> <span class="token class-name">Vector1D</span> <span class="token punctuation">{</span> y<span class="token operator">:</span> <span class="token builtin">number</span> <span class="token punctuation">}</span>
<span class="token keyword">interface</span> <span class="token class-name">Vector3D</span> <span class="token keyword">extends</span> <span class="token class-name">Vector2D</span> <span class="token punctuation">{</span> z<span class="token operator">:</span> <span class="token builtin">number</span> <span class="token punctuation">}</span>
</code></pre></div><p>\u8FD9\u91CC\u4F60\u53EF\u4EE5\u8BF4 <code>Vector3D</code> \u662F <code>Vector2D</code> \u7684\u5B50\u7C7B\u578B\uFF0C<code>Vector2D</code> \u53C8\u662F <code>Vector1D</code> \u7684\u5B50\u7C7B\u578B\uFF0C\u4ECE\u96C6\u5408\u7684\u89D2\u5EA6\uFF0C\u53EF\u4EE5\u7528\u97E6\u6069\u56FE\u5F62\u8C61\u7684\u63CF\u8FF0\u5B83\u4EEC\u4E4B\u95F4\u7684\u5173\u7CFB\uFF1A <img src="`+r+`" class="mx-auto mt-20px"></p><p>\u6211\u4EEC\u4E0A\u9762\u90FD\u662F\u9488\u5BF9\u5BF9\u8C61\u7C7B\u578B\u4F7F\u7528\u96C6\u5408\u7684\u6982\u5FF5\u8FDB\u884C\u89E3\u91CA\uFF0C\u5982\u679C\u662F\u5B57\u9762\u7C7B\u578B\uFF08literal type\uFF09\u4EE5\u53CA\u5B83\u4EEC\u7684\u8054\u5408\u7C7B\u578B\uFF0C\u91C7\u7528\u96C6\u5408\u7684\u89E3\u91CA\u4F1A\u66F4\u52A0\u76F4\u89C2\u3002<code>extends</code> \u4E5F\u53EF\u505A\u4E3A\u6CDB\u578B\u7684\u7EA6\u675F\u51FA\u73B0\uFF0C\u5728\u8FD9\u79CD\u573A\u666F\u4E0B\uFF0C\u5B83\u4E5F\u662F\u5B50\u96C6\uFF08subset\uFF09\u7684\u610F\u601D\uFF1A</p><div class="language-ts"><pre><code><span class="token keyword">function</span> <span class="token generic-function"><span class="token function">getKey</span><span class="token generic class-name"><span class="token operator">&lt;</span><span class="token constant">K</span> <span class="token keyword">extends</span> <span class="token builtin">string</span><span class="token operator">&gt;</span></span></span><span class="token punctuation">(</span>val<span class="token operator">:</span> <span class="token builtin">any</span><span class="token punctuation">,</span> key<span class="token operator">:</span> <span class="token constant">K</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token comment">// ...</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\u8FD9\u91CC\u7684 <code>extends string</code> \u662F\u4EC0\u4E48\u610F\u601D\uFF1F\u5982\u679C\u4F60\u4ECE\u5BF9\u8C61\u7EE7\u627F\u7684\u89D2\u5EA6\u6765\u601D\u8003\uFF0C\u90A3\u5C31\u5F88\u96BE\u89E3\u91CA\uFF0C\u800C\u5982\u679C\u4ECE\u96C6\u5408\u7684\u89D2\u5EA6\u6765\u8003\u8651\uFF0C\u5C31\u5F88\u6E05\u695A\u4E86\uFF1A<code>K</code> \u4EE3\u8868\u7684\u96C6\u5408\u5FC5\u987B\u662F <code>string</code> \u7684\u5B50\u96C6\u3002\u8FD9\u5305\u62EC\u5B57\u7B26\u4E32\u5B57\u9762\u7C7B\u578B\uFF08string literal type\uFF09\u3001\u5B57\u7B26\u4E32\u5B57\u9762\u7C7B\u578B\u7684\u8054\u5408\u7C7B\u578B\uFF08union of string literal types\uFF09\u548C <code>string</code> \u672C\u8EAB\u3002</p><div class="language-ts"><pre><code><span class="token function">getKey</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token punctuation">}</span><span class="token punctuation">,</span> <span class="token string">&#39;x&#39;</span><span class="token punctuation">)</span> <span class="token comment">// OK, &#39;x&#39; extends string</span>
<span class="token function">getKey</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token punctuation">}</span><span class="token punctuation">,</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">&lt;</span> <span class="token number">0.5</span> <span class="token operator">?</span> <span class="token string">&#39;a&#39;</span> <span class="token operator">:</span> <span class="token string">&#39;b&#39;</span><span class="token punctuation">)</span> <span class="token comment">// OK, &#39;a&#39;|&#39;b&#39; extends string</span>
<span class="token function">getKey</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token punctuation">}</span><span class="token punctuation">,</span> document<span class="token punctuation">.</span>title<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// OK, string extends string</span>
<span class="token function">getKey</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token punctuation">}</span><span class="token punctuation">,</span> <span class="token number">12</span><span class="token punctuation">)</span>
 <span class="token comment">// ~~ Type &#39;12&#39; is not assignable to parameter of type &#39;string&#39;</span>
</code></pre></div><p>\u5982\u679C\u628A\u7C7B\u578B\u770B\u4F5C\u503C\u7684\u96C6\u5408\uFF0C\u8FD9\u610F\u5473\u7740\u5177\u6709\u76F8\u540C\u503C\u96C6\u5408\u7684\u4E24\u4E2A\u7C7B\u578B\u662F\u76F8\u540C\u7684\u3002\u5B9E\u9645\u4E0A\u5C31\u662F\u8FD9\u6837\uFF0C\u9664\u975E\u4E24\u4E2A\u7C7B\u578B\u5728\u8BED\u4E49\u4E0A\u662F\u4E0D\u540C\u7684\uFF0C\u53EA\u662F\u78B0\u5DE7\u6709\u76F8\u540C\u7684\u503C\u96C6\u5408\uFF0C\u5426\u5219\u6CA1\u6709\u7406\u7531\u5C06\u540C\u4E00\u4E2A\u7C7B\u578B\u5B9A\u4E49\u4E24\u6B21\u3002</p><p>\u6700\u540E\uFF0C\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C\u5E76\u975E\u6240\u6709\u7684\u503C\u96C6\u90FD\u6709\u5BF9\u5E94\u7684 TypeScript \u7C7B\u578B\u3002\u6240\u6709\u6574\u6570\u7EC4\u6210\u7684\u96C6\u5408\u5C31\u6CA1\u6709\u5BF9\u5E94\u7684 TypeScript \u7C7B\u578B\uFF0C\u6240\u6709\u53EA\u5305\u542B x \u548C y \u5C5E\u6027\u7684\u5BF9\u8C61\u7EC4\u6210\u7684\u96C6\u5408\uFF0C\u4E5F\u627E\u4E0D\u5230\u5BF9\u5E94\u7684 TypeScript \u7C7B\u578B\u3002</p><p>TypeScript \u4E2D\u7684\u672F\u8BED\u4E0E\u96C6\u5408\u8BBA\u4E2D\u7684\u672F\u8BED\u7684\u6709\u5982\u4E0B\u5BF9\u5E94\u5173\u7CFB\uFF1A</p><table><thead><tr><th>TypeScript term</th><th>Set term</th></tr></thead><tbody><tr><td>never</td><td>\u2205 (empty set)</td></tr><tr><td>Literal type</td><td>Single element set</td></tr><tr><td>Value assignable to T</td><td>Value \u2208 T (member of)</td></tr><tr><td>T1 assignable to T2</td><td>T1 \u2286 T2 (subset of)</td></tr><tr><td>T1 extends T2</td><td>T1 \u2286 T2 (subset of)</td></tr><tr><td>T1 | T2</td><td>T1 \u222A T2 (union)</td></tr><tr><td>T1 &amp; T2</td><td>T1 \u2229 T2 (intersection)</td></tr><tr><td>unknown</td><td>Universal set</td></tr></tbody></table><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><ul><li>\u628A\u7C7B\u578B\u770B\u4F5C\u662F\u7C7B\u578B\u7684\u96C6\u5408\uFF0C\u8FD9\u4E9B\u96C6\u5408\u53EF\u4EE5\u662F\u6709\u9650\u96C6\uFF08boolean\uFF0Cliteral types\uFF09\u6216\u8005\u662F\u65E0\u9650\u96C6\uFF08number\uFF0Cstring\uFF09\u3002</li><li>\u8BF7\u8BB0\u4F4F\uFF0C\u4E00\u4E2A\u5BF9\u8C61\u4ECD\u7136\u53EF\u4EE5\u5C5E\u4E8E\u4E00\u4E2A\u7C7B\u578B\uFF0C\u5373\u4F7F\u5B83\u6709\u5728\u7C7B\u578B\u58F0\u660E\u4E2D\u6CA1\u6709\u63D0\u5230\u7684\u989D\u5916\u5C5E\u6027\u3002</li><li>\u7406\u89E3\u7C7B\u578B\u64CD\u4F5C\u662F\u4F5C\u7528\u5728\u7C7B\u578B\u4EE3\u8868\u7684\u96C6\u5408\u4E0A\u7684\u3002</li><li>\u628A \u201Dextends\u201C\uFF0C\u201Cassignable to\u201D\uFF0C\u201Csubtype of\u201D \u770B\u4F5C\u662F \u201Dsubset of\u201C\uFF08\u5B50\u96C6\uFF09\u7684\u540C\u4E49\u8BCD\u3002</li></ul>`,45);function u(m,y,g,b,f,v){const n=c("PubDate");return l(),e("div",null,[d,t(n,{date:"2021/03/06"}),i])}var h=a(k,[["render",u]]);export{T as __pageData,h as default};
