import{_ as s,c as t,b as o,d as n,e,a as p,r as c,o as l}from"./app.144f2fe4.js";var u="/yangss/assets/blob.c24a98df.svg";const h='{"title":"Blob \u662F\u4EC0\u4E48\uFF1F","description":"","frontmatter":{},"headers":[{"level":2,"title":"Blob \u8F6C URL","slug":"blob-\u8F6C-url"},{"level":2,"title":"Blob \u8F6C Base64","slug":"blob-\u8F6C-base64"},{"level":2,"title":"\u4E3A image \u751F\u6210 Blob","slug":"\u4E3A-image-\u751F\u6210-blob"},{"level":2,"title":"Blob \u8F6C ArrayBuffer","slug":"blob-\u8F6C-arraybuffer"},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3"}],"relativePath":"articles/what-is-blob.md"}',r={},i=n("h1",{id:"blob-\u662F\u4EC0\u4E48\uFF1F",tabindex:"-1"},[e("Blob \u662F\u4EC0\u4E48\uFF1F "),n("a",{class:"header-anchor",href:"#blob-\u662F\u4EC0\u4E48\uFF1F","aria-hidden":"true"},"#")],-1),k=p('<p>\u524D\u9762\u8BB2\u5230\u4E86 <code>ArrayBuffer</code> \u548C <code>TypedArray</code> \u7B49\u6982\u5FF5\uFF0C\u5B83\u4EEC\u5C5E\u4E8E ECMA \u6807\u51C6\uFF0C\u662F JavaScript \u7684\u4E00\u90E8\u5206\u3002</p><p>\u800C\u5728\u6D4F\u89C8\u5668\u4E2D\uFF0C\u4E5F\u6709\u4E00\u4E2A\u7528\u4E8E\u63CF\u8FF0\u4E8C\u8FDB\u5236\u6570\u636E\u7684\u5BF9\u8C61\uFF0C\u5373 <code>Blob</code> (Binary large object)\u3002\u5B83\u662F\u4E00\u4E2A\u4E0D\u53EF\u53D8\u7684\u5305\u542B\u539F\u59CB\u6570\u636E\u7684\u7C7B\u6587\u4EF6\u5BF9\u8C61\u3002<code>Blob</code> \u7684\u4E3B\u8981\u4F5C\u7528\u662F\u65B9\u4FBF\u5728\u6D4F\u89C8\u5668\u4E2D\u8FDB\u884C\u6587\u4EF6\u64CD\u4F5C\uFF0C\u4F8B\u5982\u4E0A\u4F20\uFF0C\u4E0B\u8F7D\uFF0C\u56FE\u7247\u5C55\u793A\u7B49\u7B49\u3002</p><p><code>Blob</code> \u4E3B\u8981\u5305\u542B\u4E24\u4E2A\u90E8\u5206\uFF0C\u4E00\u4E2A\u53EF\u9009\u7684\u5B57\u7B26\u4E32\u7C7B\u578B\u7684 <code>type</code>\uFF08\u901A\u5E38\u662F MIME-type\uFF09\u548C\u4E00\u4E2A\u5305\u542B <code>ArrayBuffer</code> / <a href="https://developer.mozilla.org/en-US/docs/Web/API/ArrayBufferView" target="_blank" rel="noopener noreferrer">ArrayBufferView</a> / <code>Blob</code> / <code>String</code> \u7684 <code>blobParts</code>\uFF1A</p><img src="'+u+`" class="mx-auto mt-20px mb-30px"><p>\u521B\u5EFA\u4E00\u4E2A <code>Blob</code> \u7684\u8BED\u6CD5\u5982\u4E0B\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">new</span> <span class="token class-name">Blob</span><span class="token punctuation">(</span>blobParts<span class="token punctuation">,</span> <span class="token punctuation">[</span>options<span class="token punctuation">]</span><span class="token punctuation">)</span>
</code></pre></div><ul><li><code>blobParts</code>\uFF1A\u4E00\u4E2A\u6570\u7EC4\uFF0C\u91CC\u9762\u53EF\u4EE5\u662F <code>ArrayBuffer</code>, ArrayBufferView, <code>Blob</code>, <code>String</code>\uFF0C\u6216\u5B83\u4EEC\u7684\u4EFB\u610F\u7EC4\u5408\u3002</li><li><code>options</code>\uFF1A\u53EF\u9009\u7684\u5BF9\u8C61 <ul><li><code>type</code>\uFF1A <code>Blob</code> \u4E2D\u5B58\u50A8\u7684\u6570\u636E\u7684 MIME-type\uFF0C\u9ED8\u8BA4\u503C\u4E3A\u7A7A\u5B57\u7B26\u4E32\u3002</li><li><code>endings</code>\uFF1A\u662F\u5426\u8F6C\u6362\u884C\u5C3E\u4EE5\u4F7F <code>Blob</code> \u5BF9\u5E94\u4E8E\u5F53\u524D\u64CD\u4F5C\u7CFB\u7EDF\u7684\u6362\u884C(<code>\\r\\n</code> \u6216 <code>\\n</code>)\u3002\u9ED8\u8BA4\u60C5\u51B5\u4E0B\u662F <code>&#39;transparent&#39;</code> (\u4E0D\u505A\u4EFB\u4F55\u64CD\u4F5C)\uFF0C\u4F46\u4E5F\u53EF\u4EE5\u662F <code>&#39;native&#39;</code> (\u8F6C\u6362)\u3002</li></ul></li></ul><p>\u4F8B\u5982\uFF0C\u4ECE string \u521B\u5EFA <code>Blob</code>\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">const</span> blob <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Blob</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&#39;&lt;html&gt;...&lt;/html&gt;&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token punctuation">{</span> <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">&#39;text/html&#39;</span> <span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p>\u4ECE typed array \u548C string \u521B\u5EFA <code>Blob</code>\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">const</span> hello <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Uint8Array</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token number">72</span><span class="token punctuation">,</span> <span class="token number">101</span><span class="token punctuation">,</span> <span class="token number">108</span><span class="token punctuation">,</span> <span class="token number">108</span><span class="token punctuation">,</span> <span class="token number">111</span><span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token comment">// &#39;Hello&#39; \u7684\u4E8C\u8FDB\u5236\u5F62\u5F0F</span>
<span class="token keyword">const</span> blob <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Blob</span><span class="token punctuation">(</span><span class="token punctuation">[</span>hello<span class="token punctuation">,</span> <span class="token string">&#39; &#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;world&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token punctuation">{</span> <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">&#39;text/plain&#39;</span> <span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p>\u6709\u4E00\u70B9\u9700\u8981\u6CE8\u610F\uFF0C\u6211\u4EEC\u65E0\u6CD5\u76F4\u63A5\u66F4\u6539 <code>Blob</code> \u5185\u7684\u6570\u636E\uFF0C\u4F46\u53EF\u4EE5\u4ECE <code>Blob</code> \u4E2D\u63D0\u53D6\u7247\u6BB5\u751F\u6210\u65B0\u7684 <code>Blob</code>\uFF1A</p><div class="language-js"><pre><code>blob<span class="token punctuation">.</span><span class="token function">slice</span><span class="token punctuation">(</span><span class="token punctuation">[</span>byteStart<span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token punctuation">[</span>byteEnd<span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token punctuation">[</span>contentType<span class="token punctuation">]</span><span class="token punctuation">)</span>
</code></pre></div><ul><li><code>byteStart</code>\uFF1A\u8D77\u59CB\u5B57\u8282\u4F4D\u7F6E\uFF0C\u9ED8\u8BA4\u4E3A 0</li><li><code>byteEnd</code>\uFF1A\u7ED3\u675F\u5B57\u8282\u4F4D\u7F6E (\u4E0D\u5305\u542B)\uFF0C\u9ED8\u8BA4\u5230 blob \u672B\u5C3E</li><li><code>contentType</code>\uFF1A\u65B0\u7684 blob \u7684\u7C7B\u578B, \u9ED8\u8BA4\u4E0E\u539F blob \u76F8\u540C</li></ul><p><code>blob.slice</code> \u7684\u7528\u6CD5\u4E0E <code>array.slice</code> \u7C7B\u4F3C\uFF0C\u540C\u6837\u652F\u6301\u8D1F\u4F4D\u7F6E\u7D22\u5F15\u3002</p><h2 id="blob-\u8F6C-url" tabindex="-1">Blob \u8F6C URL <a class="header-anchor" href="#blob-\u8F6C-url" aria-hidden="true">#</a></h2><p>\u524D\u9762\u8BF4\u5230 <code>Blob</code> \u4E3A\u6587\u4EF6\u800C\u751F\uFF0C\u6240\u4EE5\u53EF\u4EE5\u5F88\u5BB9\u6613\u7684\u5C06 <code>Blob</code> \u4F7F\u7528\u4E8E <code>&lt;a&gt;</code>, <code>&lt;img&gt;</code> \u6216\u5176\u5B83\u63A5\u53D7 url \u7684\u6807\u7B7E\uFF0C\u7528\u4E8E\u5C55\u793A\u5176\u5185\u5BB9\u3002</p><p>\u8FD9\u6B63\u662F\u56E0\u4E3A <code>Blob</code> \u5305\u542B\u4E86\u4E00\u4E2A <code>type</code>\uFF0C\u7528\u4E8E\u6307\u5B9A\u6240\u5B58\u50A8\u7684\u6570\u636E\u7C7B\u578B (MIME-type)\uFF0C\u6709\u4E86\u8FD9\u4E2A\u7C7B\u578B\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u6B63\u786E\u7684\u4E0B\u8F7D/\u4E0A\u4F20 <code>Blob</code> \u5BF9\u8C61\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7\u7F51\u7EDC\u8BF7\u6C42\u53D1\u9001 <code>Blob</code>\uFF0C\u8FD9\u65F6 <code>type</code> \u4F1A\u81EA\u52A8\u6210\u4E3A <code>Content-Type</code>\u3002</p><p>\u770B\u4E0B\u9762\u8FD9\u4E2A\u4F8B\u5B50\uFF0C\u7528\u6237\u53EF\u4EE5\u901A\u8FC7\u70B9\u51FB\u94FE\u63A5\u6765\u4E0B\u8F7D\u4E00\u4E2A\u5305\u542B <code>&#39;Hello, world&#39;</code> \u7684\u6587\u672C\u6587\u4EF6\uFF1A</p><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>a</span> <span class="token attr-name">download</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>hello.txt<span class="token punctuation">&quot;</span></span> <span class="token attr-name">id</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>link<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>Download<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>a</span><span class="token punctuation">&gt;</span></span>

<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span><span class="token punctuation">&gt;</span></span><span class="token script"><span class="token language-javascript">
<span class="token keyword">const</span> blob <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Blob</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&#39;Hello, world&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token punctuation">{</span> <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">&#39;text/plain&#39;</span> <span class="token punctuation">}</span><span class="token punctuation">)</span>
<span class="token keyword">const</span> link <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">querySelector</span><span class="token punctuation">(</span><span class="token string">&#39;#link&#39;</span><span class="token punctuation">)</span>
link<span class="token punctuation">.</span>href <span class="token operator">=</span> <span class="token constant">URL</span><span class="token punctuation">.</span><span class="token function">createObjectURL</span><span class="token punctuation">(</span>blob<span class="token punctuation">)</span>
</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">&gt;</span></span>
</code></pre></div><p>\u4E5F\u53EF\u4EE5\u901A\u8FC7 JavaScript \u52A8\u6001\u751F\u6210 link\uFF0C\u7136\u540E\u901A\u8FC7 <code>link.click()</code> \u6A21\u62DF\u70B9\u51FB\u884C\u4E3A\uFF0C\u8FD9\u6837\u65E0\u9700\u4EFB\u4F55 HTML\uFF0C\u53EF\u4EE5\u5B9E\u73B0\u81EA\u52A8\u4E0B\u8F7D\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">const</span> link <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">createElement</span><span class="token punctuation">(</span><span class="token string">&#39;a&#39;</span><span class="token punctuation">)</span>

link<span class="token punctuation">.</span>download <span class="token operator">=</span> <span class="token string">&#39;hello.txt&#39;</span>

<span class="token keyword">const</span> blob <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Blob</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&#39;Hello, world&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token punctuation">{</span> <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">&#39;text/plain&#39;</span> <span class="token punctuation">}</span><span class="token punctuation">)</span>

link<span class="token punctuation">.</span>href <span class="token operator">=</span> <span class="token constant">URL</span><span class="token punctuation">.</span><span class="token function">createObjectURL</span><span class="token punctuation">(</span>blob<span class="token punctuation">)</span>

link<span class="token punctuation">.</span><span class="token function">click</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

<span class="token constant">URL</span><span class="token punctuation">.</span><span class="token function">revokeObjectURL</span><span class="token punctuation">(</span>link<span class="token punctuation">.</span>href<span class="token punctuation">)</span>
</code></pre></div><p>\u8FD9\u91CC\u7528\u5230\u4E86 <code>URL.createObjectURL</code> \u548C <code>URL.revokeObjectURL</code> \u4E24\u4E2A\u6D4F\u89C8\u5668\u5185\u7F6E\u65B9\u6CD5\u3002</p><p><code>URL.createObjectURL</code> \u63A5\u6536\u4E00\u4E2A <code>Blob</code> \u5BF9\u8C61\uFF0C\u5E76\u4E3A\u5B83\u751F\u6210\u4E00\u4E2A\u552F\u4E00\u7684 URL (<code>blob:&lt;origin&gt;/&lt;uuid&gt;</code>)\u3002</p><p>\u5BF9\u6BCF\u4E2A <code>URL.createObjectURL</code> \u751F\u6210\u7684 URL\uFF0C\u6D4F\u89C8\u5668\u4F1A\u5728\u5185\u90E8\u5B58\u50A8\u4E00\u4E2A URL -&gt; Blob \u7684\u6620\u5C04\u3002\u8FD9\u6837\u53EF\u4EE5\u901A\u8FC7\u5F88\u7B80\u77ED\u7684 URL \u5C31\u80FD\u8BBF\u95EE\u5230 <code>Blob</code>\u3002\u800C\u4E14\u8BE5 URL \u53EA\u5728\u5F53\u524D\u6253\u5F00\u7684\u6587\u6863\u4E2D\u6709\u6548\uFF0C\u5373\u4F5C\u7528\u57DF\u4E3A\u5F53\u524D session\u3002</p><p>\u4F46\u540C\u65F6\u8FD9\u4E5F\u6709\u4E00\u4E2A\u526F\u4F5C\u7528\u3002\u7531\u4E8E\u5B58\u5728 URL -&gt; Blob \u6620\u5C04\uFF0C <code>Blob</code> \u672C\u8EAB\u4F1A\u9A7B\u7559\u5728\u5185\u5B58\u4E2D\u3002\u9664\u975E\u9875\u9762\u88AB\u5378\u8F7D\uFF0C\u5426\u5219\u6D4F\u89C8\u5668\u65E0\u6CD5\u81EA\u52A8\u91CA\u653E\u5B83\u3002\u8FD9\u53EF\u80FD\u4E0D\u662F\u6211\u4EEC\u60F3\u8981\u7684\u3002</p><p>\u901A\u8FC7\u8C03\u7528 <code>URL.revokeObjectURL(url)</code> \u53EF\u4EE5\u4ECE\u5185\u90E8\u6620\u5C04\u4E2D\u5220\u9664\u5BF9 <code>Blob</code> \u7684\u5F15\u7528\uFF0C\u5982\u679C <code>Blob</code> \u6CA1\u6709\u5176\u5B83\u5F15\u7528\uFF0C\u5B83\u5C06\u4F1A\u88AB\u6E05\u9664\uFF0C\u5E76\u91CA\u653E\u5185\u5B58\u3002</p><p>\u5728\u4E0A\u9762\u81EA\u52A8\u4E0B\u8F7D\u7684\u4F8B\u5B50\u4E2D\uFF0C\u56E0\u4E3A\u53EA\u9700\u8981\u4E0B\u8F7D\u4E00\u6B21\uFF0C\u6240\u4EE5\u540E\u9762\u7ACB\u523B\u8C03\u7528\u4E86 <code>URL.revokeObjectURL</code> \u6765\u91CA\u653E\u5185\u5B58\u3002\u4F46\u5728\u624B\u52A8\u70B9\u51FB\u94FE\u63A5\u8FDB\u884C\u4E0B\u8F7D\u7684\u4F8B\u5B50\u4E2D\uFF0C\u6211\u4EEC\u5E0C\u671B\u53EF\u4EE5\u591A\u6B21\u4E0B\u8F7D\uFF0C\u6240\u4EE5\u6CA1\u6709\u8C03\u7528 <code>URL.revokeObjectURL</code>\uFF0C\u5982\u679C\u8FD9\u6837\u505A\uFF0C\u94FE\u63A5\u4E0A\u7684 <code>Blob</code> url \u5C06\u4F1A\u5931\u6548\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u4E4B\u95F4\u7684\u6620\u5C04\u5173\u7CFB\u5DF2\u7ECF\u88AB\u79FB\u9664\u4E86\u3002</p><h2 id="blob-\u8F6C-base64" tabindex="-1">Blob \u8F6C Base64 <a class="header-anchor" href="#blob-\u8F6C-base64" aria-hidden="true">#</a></h2><p>\u9664\u4E86\u53EF\u4EE5\u901A\u8FC7 <code>URL.createObjectURL</code> \u751F\u6210 <code>Blob</code> url \u5916\uFF0C\u8FD8\u53EF\u4EE5\u5C06 <code>Blob</code> \u8F6C\u6362\u6210 base64 \u7F16\u7801\u7684\u5B57\u7B26\u4E32\u3002</p><p>\u8FD9\u79CD\u7F16\u7801\u65B9\u5F0F\u5C06\u4E8C\u8FDB\u5236\u6570\u636E\u8868\u793A\u4E3A\u4E00\u4E32\u5B89\u5168\u7684\u201C\u53EF\u8BFB\u201D\u5B57\u7B26\u3002\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5728 <a href="https://developer.mozilla.org/en-US/docs/Web/HTTP/Basics_of_HTTP/Data_URIs" target="_blank" rel="noopener noreferrer">Data URL</a> \u4E2D\u4F7F\u7528\u8FD9\u79CD\u7F16\u7801\u3002</p><p>\u4E00\u4E2A Data URL \u5177\u6709\u8FD9\u6837\u7684\u5F62\u5F0F\uFF1A<code>data:[&lt;mediatype&gt;][;base64],&lt;data&gt;</code>\u3002\u6211\u4EEC\u53EF\u4EE5\u5728\u4EFB\u4F55\u5730\u65B9\u4F7F\u7528\u8FD9\u6837\u7684 url\uFF0C\u5C31\u50CF\u5E38\u89C4 url \u4E00\u6837\u3002\u4F8B\u5982</p><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>data:image/png;base64,R0lGODlhDAAMAKIFAF5LAP/zxAAAANyuAP/gaP///wAAAAAAACH5BAEAAAUALAAAAAAMAAwAAAMlWLPcGjDKFYi9lxKBOaGcF35DhWHamZUW0K4mAbiwWtuf0uxFAgA7<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
</code></pre></div><p>\u6D4F\u89C8\u5668\u4F1A\u89E3\u7801\u8FD9\u4E2A\u5B57\u7B26\u4E32\u5E76\u5C55\u793A\u8FD9\u5F20\u56FE\u7247<img class="inline-block ml-1" src="data:image/png;base64,R0lGODlhDAAMAKIFAF5LAP/zxAAAANyuAP/gaP///wAAAAAAACH5BAEAAAUALAAAAAAMAAwAAAMlWLPcGjDKFYi9lxKBOaGcF35DhWHamZUW0K4mAbiwWtuf0uxFAgA7">\u3002</p><p>\u8981\u5C06 <code>Blob</code> \u8F6C\u6362\u6210 base64\uFF0C\u53EF\u4EE5\u4F7F\u7528\u6D4F\u89C8\u5668\u5185\u7F6E\u7684 <a href="https://developer.mozilla.org/en-US/docs/Web/API/FileReader" target="_blank" rel="noopener noreferrer"><code>FileReader</code> API</a>\uFF0C\u5B83\u80FD\u4EE5\u591A\u79CD\u4E0D\u540C\u7684\u683C\u5F0F\u4ECE <code>Blob</code> \u79CD\u8BFB\u53D6\u6570\u636E\u3002</p><p>\u4E0B\u9762\u662F\u4F7F\u7528 base64 \u7684\u5F62\u5F0F\u4E0B\u8F7D <code>Blob</code> \u7684\u4F8B\u5B50\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">const</span> link <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">createElement</span><span class="token punctuation">(</span><span class="token string">&#39;a&#39;</span><span class="token punctuation">)</span>

link<span class="token punctuation">.</span>download <span class="token operator">=</span> <span class="token string">&#39;hello.txt&#39;</span>

<span class="token keyword">const</span> blob <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Blob</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&#39;Hello, world!&#39;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token punctuation">{</span> <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">&#39;text/plain&#39;</span> <span class="token punctuation">}</span><span class="token punctuation">)</span>

<span class="token keyword">const</span> reader <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">FileReader</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

reader<span class="token punctuation">.</span><span class="token function">readAsDataURL</span><span class="token punctuation">(</span>blob<span class="token punctuation">)</span> <span class="token comment">// \u4EE5 base64 \u7684\u683C\u5F0F\u4ECE blob \u4E2D\u8BFB\u53D6\u6570\u636E</span>

reader<span class="token punctuation">.</span><span class="token function-variable function">onload</span> <span class="token operator">=</span> <span class="token keyword">function</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  link<span class="token punctuation">.</span>href <span class="token operator">=</span> reader<span class="token punctuation">.</span>result <span class="token comment">// data url</span>
  link<span class="token punctuation">.</span><span class="token function">click</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\u8FD9\u4E24\u79CD\u521B\u5EFA <code>Blob</code> URL \u7684\u65B9\u6CD5\u90FD\u662F\u53EF\u884C\u7684\u3002\u4F46\u901A\u5E38 <code>URL.createObjectURL(blob)</code> \u66F4\u7B80\u5355\u800C\u4E14\u901F\u5EA6\u66F4\u5FEB\u3002\u4E24\u8005\u5BF9\u6BD4\u5982\u4E0B\uFF1A</p><table><thead><tr><th>URL.createObjectURL(blob)</th><th>Blob to data url</th></tr></thead><tbody><tr><td>\u5982\u679C\u5BF9\u5185\u5B58\u6CC4\u9732\u6BD4\u8F83\u5728\u610F\uFF0C\u9700\u8981\u624B\u52A8 revoke \u8FDB\u884C\u6E05\u7406</td><td>\u65E0\u5185\u5B58\u6CC4\u6F0F\u95EE\u9898\uFF0C\u65E0\u9700\u624B\u52A8\u6E05\u7406</td></tr><tr><td>\u76F4\u63A5\u8BBF\u95EE <code>Blob</code>\uFF0C\u6CA1\u6709 encoding / decoding \u8FC7\u7A0B</td><td>\u9700\u8981\u8FDB\u884C base64 \u7F16\u7801\uFF0C\u5F53 <code>Blob</code> \u5F88\u5927\u65F6\uFF0C\u4F1A\u6709\u6027\u80FD\u95EE\u9898\uFF0C\u800C\u4E14\u4F1A\u5360\u7528\u66F4\u5927\u7684\u5185\u5B58\u7A7A\u95F4</td></tr></tbody></table><h2 id="\u4E3A-image-\u751F\u6210-blob" tabindex="-1">\u4E3A image \u751F\u6210 Blob <a class="header-anchor" href="#\u4E3A-image-\u751F\u6210-blob" aria-hidden="true">#</a></h2><p>\u4E5F\u53EF\u4EE5\u4E3A\u56FE\u7247\u751F\u6210 <code>Blob</code>\uFF0C\u7136\u540E\u8FDB\u884C\u4E0A\u4F20\u548C\u4E0B\u8F7D\u7B49\u7B49\u3002</p><p>\u5BF9\u56FE\u7247\u7684\u64CD\u4F5C\u53EF\u4EE5\u501F\u52A9 <a href="https://developer.mozilla.org/en-US/docs/Web/API/Canvas_API/Tutorial" target="_blank" rel="noopener noreferrer"><code>canvas</code></a> \u5143\u7D20\u5B9E\u73B0</p><ul><li>\u4F7F\u7528 <a href="https://developer.mozilla.org/en-US/docs/Web/api/CanvasRenderingContext2D/drawImage" target="_blank" rel="noopener noreferrer"><code>canvas.drawImage</code></a> \u5C06\u56FE\u7247\u753B\u5728 canvas \u753B\u5E03\u4E0A\u3002</li><li>\u8C03\u7528 <a href="https://developer.mozilla.org/en-US/docs/Web/api/HTMLCanvasElement/toBlob" target="_blank" rel="noopener noreferrer"><code>.toBlob(callback, type, quality)</code></a> \u83B7\u53D6 <code>Blob</code>\uFF0C\u7B49\u5F85\u5B8C\u6210\u540E\u4F1A\u6267\u884C <code>callback</code> \u56DE\u8C03\u3002</li></ul><div class="language-js"><pre><code><span class="token comment">// \u83B7\u53D6\u56FE\u7247\u5143\u7D20</span>
<span class="token keyword">const</span> img <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">querySelector</span><span class="token punctuation">(</span><span class="token string">&#39;img&#39;</span><span class="token punctuation">)</span>

<span class="token comment">// \u521B\u5EFA\u4E00\u4E2A\u76F8\u540C\u5C3A\u5BF8\u7684\u753B\u5E03</span>
<span class="token keyword">const</span> canvas <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">createElement</span><span class="token punctuation">(</span><span class="token string">&#39;canvas&#39;</span><span class="token punctuation">)</span>
canvas<span class="token punctuation">.</span>width <span class="token operator">=</span> img<span class="token punctuation">.</span>clientWidth
canvas<span class="token punctuation">.</span>height <span class="token operator">=</span> img<span class="token punctuation">.</span>clientHeight

<span class="token keyword">const</span> context <span class="token operator">=</span> canvas<span class="token punctuation">.</span><span class="token function">getContext</span><span class="token punctuation">(</span><span class="token string">&#39;2d&#39;</span><span class="token punctuation">)</span>

<span class="token comment">// \u590D\u5236\u56FE\u7247\u5230\u753B\u5E03 (\u8FD8\u53EF\u80FD\u901A\u8FC7\u8FD9\u4E2A\u65B9\u6CD5\u5BF9\u56FE\u7247\u8FDB\u884C\u88C1\u526A)</span>
context<span class="token punctuation">.</span><span class="token function">drawImage</span><span class="token punctuation">(</span>img<span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">)</span>

<span class="token comment">// toBlob \u662F\u5F02\u6B65\u64CD\u4F5C, callback \u4F1A\u5728\u5B8C\u6210\u540E\u6267\u884C</span>
canvas<span class="token punctuation">.</span><span class="token function">toBlob</span><span class="token punctuation">(</span><span class="token keyword">function</span><span class="token punctuation">(</span><span class="token parameter">blob</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token comment">// \u62FF\u5230 blob\uFF0C\u8FDB\u884C\u4E0B\u8F7D</span>
  <span class="token keyword">const</span> link <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">createElement</span><span class="token punctuation">(</span><span class="token string">&#39;a&#39;</span><span class="token punctuation">)</span>
  link<span class="token punctuation">.</span>download <span class="token operator">=</span> <span class="token string">&#39;example.png&#39;</span>

  link<span class="token punctuation">.</span>href <span class="token operator">=</span> <span class="token constant">URL</span><span class="token punctuation">.</span><span class="token function">createObjectURL</span><span class="token punctuation">(</span>blob<span class="token punctuation">)</span>
  link<span class="token punctuation">.</span><span class="token function">click</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

  <span class="token comment">// \u5220\u9664\u5185\u90E8 blob \u5F15\u7528, \u8BA9\u6D4F\u89C8\u5668\u91CA\u653E\u5185\u5B58</span>
  <span class="token constant">URL</span><span class="token punctuation">.</span><span class="token function">revokeObjectURL</span><span class="token punctuation">(</span>link<span class="token punctuation">.</span>href<span class="token punctuation">)</span>
<span class="token punctuation">}</span><span class="token punctuation">,</span> <span class="token string">&#39;image/png&#39;</span><span class="token punctuation">)</span>
</code></pre></div><h2 id="blob-\u8F6C-arraybuffer" tabindex="-1">Blob \u8F6C ArrayBuffer <a class="header-anchor" href="#blob-\u8F6C-arraybuffer" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528 <code>Blob()</code> \u6784\u9020\u51FD\u6570\u4ECE\u51E0\u4E4E\u4EFB\u4F55\u4E1C\u897F\u521B\u5EFA\u4E00\u4E2A blob\uFF0C\u5305\u62EC <code>ArrayBuffer</code> \u548C\u6240\u6709 <code>ArrayBufferView</code>\u3002</p><p>\u4F46\u5982\u679C\u6211\u4EEC\u8981\u884C\u8FDB low-level \u7684\u64CD\u4F5C\uFF0C\u53EF\u4EE5\u4F7F\u7528 <code>FileReader</code> \u83B7\u53D6 <code>Blob</code> \u6700\u5E95\u5C42\u7684 <code>ArrayBuffer</code>\uFF1A</p><div class="language-js"><pre><code><span class="token comment">// \u4ECE Blob \u83B7\u53D6 ArrayBuffer</span>
<span class="token keyword">const</span> fileReader <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">FileReader</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

fileReader<span class="token punctuation">.</span><span class="token function">readAsArrayBuffer</span><span class="token punctuation">(</span>blob<span class="token punctuation">)</span>

fileReader<span class="token punctuation">.</span><span class="token function-variable function">onload</span> <span class="token operator">=</span> <span class="token keyword">function</span><span class="token punctuation">(</span><span class="token parameter">event</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token keyword">const</span> arrayBuffer <span class="token operator">=</span> fileReader<span class="token punctuation">.</span>result
<span class="token punctuation">}</span>
</code></pre></div><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><ul><li><code>ArrayBuffer</code>, <code>Uint8Array </code> \u7B49\u7B49\u5176\u5B83 <code>ArrayBufferView</code> \u90FD\u5C5E\u4E8E\u4E8C\u8FDB\u5236\u6570\u636E\uFF0C\u800C <code>Blob</code> \u4EE3\u8868\u7684\u662F\u6709\u7C7B\u578B (type) \u7684\u4E8C\u8FDB\u5236\u6570\u636E\u3002</li><li>\u56E0\u4E3A <code>Blob</code> \u5177\u6709\u7C7B\u578B\uFF0C\u8FD9\u4F7F\u5F97\u5B83\u53EF\u4EE5\u65B9\u4FBF\u5730\u8FDB\u884C\u4E0A\u4F20/\u4E0B\u8F7D\u64CD\u4F5C\uFF0C\u8FD9\u4E9B\u64CD\u4F5C\u5728\u6D4F\u89C8\u5668\u4E2D\u975E\u5E38\u5E38\u89C1\u3002</li><li><code>XMLHttpRequest</code>, <code>fetch</code> web\u8BF7\u6C42\u539F\u751F\u5730\u652F\u6301 Blob \u7C7B\u578B\u3002</li><li><code>Blob</code> \u548C low-level \u7684\u4E8C\u8FDB\u5236\u6570\u636E\u7C7B\u578B\u53EF\u4EE5\u8FDB\u884C\u76F8\u4E92\u8F6C\u6362\u3002</li></ul>`,50);function d(b,g,f,m,A,y){const a=c("PubDate");return l(),t("div",null,[i,o(a,{date:"2021/11/22"}),k])}var v=s(r,[["render",d]]);export{h as __pageData,v as default};
